// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import "./AggregatorBase.sol";
import "../interfaces/ISignatureAggregator.sol";
import "../libraries/SignatureUtil.sol";

contract SignatureAggregator is AggregatorBase, ISignatureAggregator {
    using SignatureUtil for bytes;
    using SignatureUtil for bytes32;

    /* ========== STATE VARIABLES ========== */

    mapping(bytes32 => DebridgeDeployInfo) public getDeployInfo; // mint id => debridge info
    mapping(bytes32 => SubmissionInfo) public getSubmissionInfo; // mint id => submission info

    /* ========== ERRORS ========== */

    error SenderSignatureMismatch();

    /* ========== CONSTRUCTOR  ========== */

    /// @dev Constructor that initializes the most important configurations.
    /// @param _minConfirmations Common confirmations count.
    /// @param _excessConfirmations Confirmations count in case of excess activity.
    function initialize(uint8 _minConfirmations, uint8 _excessConfirmations) public initializer {
        AggregatorBase.initializeBase(_minConfirmations, _excessConfirmations);
    }

    /* ========== ORACLES  ========== */

    /// @dev Confirms the transfer request.
    function confirmNewAsset(
        bytes memory _tokenAddress,
        uint256 _chainId,
        string memory _name,
        string memory _symbol,
        uint8 _decimals,
        bytes memory _signature
    ) external onlyOracle {
        bytes32 debridgeId = getDebridgeId(_chainId, _tokenAddress);
        bytes32 deployId = getDeployId(debridgeId, _name, _symbol, _decimals);
        DebridgeDeployInfo storage debridgeInfo = getDeployInfo[deployId];

        if (debridgeInfo.hasVerified[msg.sender]) revert SubmittedAlready();

        _checkSignature(_signature, deployId);

        debridgeInfo.confirmations += 1;
        debridgeInfo.nativeAddress = _tokenAddress;
        debridgeInfo.chainId = _chainId;
        debridgeInfo.name = _name;
        debridgeInfo.symbol = _symbol;
        debridgeInfo.decimals = _decimals;
        debridgeInfo.signatures.push(_signature);
        debridgeInfo.hasVerified[msg.sender] = true;
        if (debridgeInfo.confirmations >= minConfirmations
            && debridgeInfo.confirmations >= excessConfirmations) {
            debridgeInfo.approved = true;
            emit DeployApproved(deployId);
        }
        emit DeployConfirmed(deployId, msg.sender, _signature);
    }

    /// @dev Confirms few transfer requests.
    /// @param _submissionIds Submission identifiers.
    /// @param _signatures Oracles signature.
    function submitMany(bytes32[] memory _submissionIds, bytes[] memory _signatures)
        external
        override
        onlyOracle
    {
        if (_submissionIds.length != _signatures.length) revert WrongArgument();
        for (uint256 i; i < _submissionIds.length; i++) {
            _submit(_submissionIds[i], _signatures[i]);
        }
    }

    /// @dev Confirms the transfer request.
    /// @param _submissionId Submission identifier.
    /// @param _signature Oracle's signature.
    function submit(bytes32 _submissionId, bytes memory _signature) external override onlyOracle {
        _submit(_submissionId, _signature);
    }

    /// @dev Confirms single transfer request.
    /// @param _submissionId Submission identifier.
    /// @param _signature Oracle's signature.
    function _submit(bytes32 _submissionId, bytes memory _signature) internal {
        SubmissionInfo storage submissionInfo = getSubmissionInfo[_submissionId];
        if (submissionInfo.hasVerified[msg.sender]) revert SubmittedAlready();

        _checkSignature(_signature, _submissionId);

        submissionInfo.confirmations += 1;
        submissionInfo.signatures.push(_signature);
        submissionInfo.hasVerified[msg.sender] = true;
        emit Confirmed(_submissionId, msg.sender, _signature);
    }

    /* ========== VIEW ========== */

    /// @dev Returns whether transfer request is confirmed.
    /// @param _submissionId Submission identifier.
    /// @return _confirmations number of confirmation.
    /// @return _confirmed Whether transfer request is confirmed.
    function getSubmissionConfirmations(bytes32 _submissionId)
        external
        view
        override
        returns (uint8 _confirmations, bool _confirmed)
    {
        SubmissionInfo storage submissionInfo = getSubmissionInfo[_submissionId];
        _confirmations = submissionInfo.confirmations;
        return (_confirmations, _confirmations >= minConfirmations);
    }

    /// @dev Returns whether transfer request is confirmed.
    /// @param _submissionId Submission identifier.
    /// @return Oracles signatures.
    function getSubmissionSignatures(bytes32 _submissionId) external view returns (bytes[] memory) {
        return getSubmissionInfo[_submissionId].signatures;
    }

    /* ========== INTERNAL ========== */

    function _checkSignature(bytes memory _signature, bytes32 _message) internal view {
        (bytes32 r, bytes32 s, uint8 v) = _signature.splitSignature();
        address oracle = ecrecover(_message.getUnsignedMsg(), v, r, s);
        if (msg.sender != oracle) revert SenderSignatureMismatch();
    }

    // ============ Version Control ============
    function version() external pure returns (uint256) {
        return 101; // 1.0.1
    }
}

contracts/interfaces/ISignatureAggregato.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.7;

interface ISignatureAggregator {
    /* ========== STRUCTS ========== */

    struct SubmissionInfo {
        uint8 confirmations; // received confirmations count
        bytes[] signatures;
        mapping(address => bool) hasVerified; // verifier => has already voted
    }
    struct DebridgeDeployInfo {
        uint256 chainId; //native chainId
        bytes nativeAddress; //native token address
        uint8 decimals;
        uint8 confirmations; // received confirmations count
        bool approved;
        string name;
        string symbol;
        bytes[] signatures;
        mapping(address => bool) hasVerified; // verifier => has already voted
    }

    /* ========== EVENTS ========== */

    event DeployConfirmed(bytes32 deployId, address operator, bytes signature); // emitted once the submission is confirmed by one oracle
    event Confirmed(bytes32 submissionId, address operator, bytes signature); // emitted once the submission is confirmed by one oracle

    /* ========== FUNCTIONS ========== */

    function submitMany(bytes32[] memory _submissionIds, bytes[] memory _signatures) external;

    function submit(bytes32 _submissionId, bytes memory _signature) external;

    function getSubmissionConfirmations(bytes32 _submissionId)
        external
        view
        returns (uint8 _confirmations, bool _blockConfirmationPassed);
}

contracts/transfers/ConfirmationAggregat.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import "./AggregatorBase.sol";
import "../interfaces/IConfirmationAggregator.sol";

contract ConfirmationAggregator is AggregatorBase, IConfirmationAggregator {
    /* ========== STATE VARIABLES ========== */

    uint8 public confirmationThreshold; // required confirmations per block after extra check enabled

    mapping(bytes32 => bytes32) public confirmedDeployInfo; // debridge Id => deploy Id
    mapping(bytes32 => DebridgeDeployInfo) public getDeployInfo; // mint id => debridge info
    mapping(bytes32 => SubmissionInfo) public getSubmissionInfo; // mint id => submission info

    uint40 public submissionsInBlock; //submissions count in current block
    uint40 public currentBlock; //Current block


    /* ========== CONSTRUCTOR  ========== */

    /// @dev Constructor that initializes the most important configurations.
    /// @param _minConfirmations Common confirmations count.
    /// @param _confirmationThreshold Confirmations per block after extra check enabled.
    /// @param _excessConfirmations Confirmations count in case of excess activity.
    function initialize(
        uint8 _minConfirmations,
        uint8 _confirmationThreshold,
        uint8 _excessConfirmations
    ) public initializer {
        AggregatorBase.initializeBase(_minConfirmations, _excessConfirmations);
        confirmationThreshold = _confirmationThreshold;
    }

    /* ========== ORACLES  ========== */

    /// @dev Confirms few transfer requests.
    /// @param _submissionIds Submission identifiers.
    function submitMany(bytes32[] memory _submissionIds) external override onlyOracle {
        for (uint256 i; i < _submissionIds.length; i++) {
            _submit(_submissionIds[i]);
        }
    }

    /// @dev Confirms the transfer request.
    function confirmNewAsset(
        bytes memory _tokenAddress,
        uint256 _chainId,
        string memory _name,
        string memory _symbol,
        uint8 _decimals
    ) external onlyOracle {
        bytes32 debridgeId = getDebridgeId(_chainId, _tokenAddress);

        bytes32 deployId = getDeployId(debridgeId, _name, _symbol, _decimals);
        DebridgeDeployInfo storage debridgeInfo = getDeployInfo[deployId];
        if (debridgeInfo.hasVerified[msg.sender]) revert SubmittedAlready();

        debridgeInfo.name = _name;
        debridgeInfo.symbol = _symbol;
        debridgeInfo.nativeAddress = _tokenAddress;
        debridgeInfo.chainId = _chainId;
        debridgeInfo.decimals = _decimals;
        debridgeInfo.confirmations += 1;
        if (getOracleInfo[msg.sender].required) {
            debridgeInfo.requiredConfirmations += 1;
        }
        debridgeInfo.hasVerified[msg.sender] = true;

        if (debridgeInfo.confirmations >= minConfirmations) {
            confirmedDeployInfo[debridgeId] = deployId;
        }

        emit DeployConfirmed(deployId, msg.sender);
    }

    /// @dev Confirms the transfer request.
    /// @param _submissionId Submission identifier.
    function submit(bytes32 _submissionId) external override onlyOracle {
        _submit(_submissionId);
    }

    /// @dev Confirms single transfer request.
    /// @param _submissionId Submission identifier.
    function _submit(bytes32 _submissionId) internal {
        SubmissionInfo storage submissionInfo = getSubmissionInfo[_submissionId];
        if (submissionInfo.hasVerified[msg.sender]) revert SubmittedAlready();

        submissionInfo.confirmations += 1;
        if (getOracleInfo[msg.sender].required) {
            submissionInfo.requiredConfirmations += 1;
        }
        submissionInfo.hasVerified[msg.sender] = true;
        if (submissionInfo.confirmations >= minConfirmations) {
            if (currentBlock != uint40(block.number)) {
                currentBlock = uint40(block.number);
                submissionsInBlock = 0;
            }
            bool requireExtraCheck = submissionsInBlock >= confirmationThreshold;

            if (
                submissionInfo.requiredConfirmations >= requiredOraclesCount &&
                !submissionInfo.isConfirmed &&
                (!requireExtraCheck ||
                    (requireExtraCheck && submissionInfo.confirmations >= excessConfirmations))
            ) {
                submissionsInBlock += 1;
                submissionInfo.isConfirmed = true;
                emit SubmissionApproved(_submissionId);
            }
        }
        emit Confirmed(_submissionId, msg.sender);
    }

    /* ========== deployAsset ========== */

    function getConfirmedDeployId(bytes32 _debridgeId) external override view returns (bytes32) {
        return confirmedDeployInfo[_debridgeId];
    }

    /* ========== ADMIN ========== */

    /// @dev Sets minimal required confirmations.
    /// @param _confirmationThreshold Confirmation info.
    function setThreshold(uint8 _confirmationThreshold) external onlyAdmin {
        if (_confirmationThreshold == 0) revert WrongArgument();
        confirmationThreshold = _confirmationThreshold;
    }

    /* ========== VIEW ========== */

    /// @dev Returns whether transfer request is confirmed.
    /// @param _submissionId Submission identifier.
    /// @return confirmations number of confirmation.
    /// @return isConfirmed is confirmed sumbission.
    function getSubmissionConfirmations(bytes32 _submissionId)
        external
        view
        override
        returns (uint8 confirmations, bool isConfirmed)
    {
        SubmissionInfo storage submissionInfo = getSubmissionInfo[_submissionId];

        return (submissionInfo.confirmations, submissionInfo.isConfirmed);
    }

    // ============ Version Control ============
    function version() external pure returns (uint256) {
        return 101; // 1.0.1
    }
}

contracts/transfers/DeBridgeTokenDeploye.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";

import "../interfaces/IDeBridgeTokenDeployer.sol";
import "../periphery/DeBridgeToken.sol";
import "../periphery/DeBridgeTokenProxy.sol";

contract DeBridgeTokenDeployer is
    Initializable,
    AccessControlUpgradeable,
    IDeBridgeTokenDeployer
{

    /* ========== STATE VARIABLES ========== */

    // address of deBridgeToken implementation
    address public tokenImplementation;
    // admin for any deployed deBridgeToken
    address public deBridgeTokenAdmin;
    // Debridge gate address
    address public debridgeAddress;
    // debridge id => deBridgeToken address
    mapping(bytes32 => address) public getDeployedAssetAddress;


    /* ========== ERRORS ========== */

    error WrongArgument();
    error DeployedAlready();

    error AdminBadRole();
    error DeBridgeGateBadRole();


    /* ========== MODIFIERS ========== */

    modifier onlyAdmin() {
        if (!hasRole(DEFAULT_ADMIN_ROLE, msg.sender)) revert AdminBadRole();
        _;
    }

    modifier onlyDeBridgeGate() {
        if (msg.sender != debridgeAddress) revert DeBridgeGateBadRole();
        _;
    }


    /* ========== CONSTRUCTOR  ========== */

    /// @dev Constructor that initializes the most important configurations.
    function initialize(
        address _tokenImplementation,
        address _deBridgeTokenAdmin,
        address _debridgeAddress
    ) public initializer {
        tokenImplementation = _tokenImplementation;
        deBridgeTokenAdmin = _deBridgeTokenAdmin;
        debridgeAddress = _debridgeAddress;

        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function deployAsset(
        bytes32 _debridgeId,
        string memory _name,
        string memory _symbol,
        uint8 _decimals)
        external
        override
        onlyDeBridgeGate
        returns (address deBridgeTokenAddress)
    {
        if (getDeployedAssetAddress[_debridgeId] != address(0)) revert DeployedAlready();

        address[] memory minters = new address[](1);
        minters[0] = debridgeAddress;

        // Initialize args
        bytes memory initialisationArgs = abi.encodeWithSelector(
            DeBridgeToken.initialize.selector,
            _name,
            _symbol,
            _decimals,
            deBridgeTokenAdmin,
            minters
        );

        // initialize Proxy
        bytes memory constructorArgs = abi.encode(address(this), initialisationArgs);

        // deployment code
        bytes memory bytecode = abi.encodePacked(type(DeBridgeTokenProxy).creationCode, constructorArgs);

        assembly {
            // debridgeId is a salt
            deBridgeTokenAddress := create2(0, add(bytecode, 0x20), mload(bytecode), _debridgeId)

            if iszero(extcodesize(deBridgeTokenAddress)) {
                revert(0, 0)
            }
        }

        getDeployedAssetAddress[_debridgeId] = deBridgeTokenAddress;
        emit DeBridgeTokenDeployed(
            deBridgeTokenAddress,
            _name,
            _symbol,
            _decimals
        );
    }

    // Beacon getter for the deBridgeToken contracts
    function implementation() public view returns (address) {
        return tokenImplementation;
    }


    /* ========== ADMIN ========== */

    /// @dev Set deBridgeToken implementation contract address
    /// @param _impl Wrapped asset implementation contract address.
    function setTokenImplementation(address _impl) external onlyAdmin {
        if (_impl == address(0)) revert WrongArgument();
        tokenImplementation = _impl;
    }

    /// @dev Set admin for any deployed deBridgeToken.
    /// @param _deBridgeTokenAdmin Admin address.
    function setDeBridgeTokenAdmin(address _deBridgeTokenAdmin) external onlyAdmin {
        if (_deBridgeTokenAdmin == address(0)) revert WrongArgument();
        deBridgeTokenAdmin = _deBridgeTokenAdmin;
    }

    /// @dev Sets core debridge conrtact address.
    /// @param _debridgeAddress Debridge address.
    function setDebridgeAddress(address _debridgeAddress) external onlyAdmin {
        if (_debridgeAddress == address(0)) revert WrongArgument();
        debridgeAddress = _debridgeAddress;
    }

    // ============ Version Control ============
    function version() external pure returns (uint256) {
        return 101; // 1.0.1
    }
}

